import React, { useState, useEffect } from 'react'
import axios from 'axios'

function App() {
  const [form, setForm] = useState({
    q_date: new Date().toISOString().split('T')[0],
    supplier: '',
    products: [
      {
        product_name: '', rate: '', discount: '', packing: '', gst: '',
        purchase_rate: '', piece_weight: '', profit_percent: ''
      }
    ]
  })

  const [quotations, setQuotations] = useState([])

  const handleChange = (e, index, field) => {
    const newProducts = [...form.products]
    newProducts[index][field] = e.target.value
    setForm({ ...form, products: newProducts })
  }

  const addProductRow = () => {
    setForm({
      ...form,
      products: [...form.products, {
        product_name: '', rate: '', discount: '', packing: '', gst: '',
        purchase_rate: '', piece_weight: '', profit_percent: ''
      }]
    })
  }

  const handleFormChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value })
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    await axios.post("http://localhost:8000/quotations", {
      ...form,
      products: form.products.map(p => ({
        ...p,
        rate: parseFloat(p.rate),
        discount: parseFloat(p.discount),
        packing: parseFloat(p.packing),
        gst: parseFloat(p.gst),
        purchase_rate: parseFloat(p.purchase_rate),
        piece_weight: parseFloat(p.piece_weight),
        profit_percent: parseFloat(p.profit_percent)
      }))
    })
    fetchQuotations()
    setForm({
      q_date: new Date().toISOString().split('T')[0],
      supplier: '',
      products: [
        {
          product_name: '', rate: '', discount: '', packing: '', gst: '',
          purchase_rate: '', piece_weight: '', profit_percent: ''
        }
      ]
    })
  }

  const fetchQuotations = async () => {
    const res = await axios.get("http://localhost:8000/quotations")
    setQuotations(res.data)
  }

  useEffect(() => {
    fetchQuotations()
  }, [])

  return (
    <div className="p-6">
      <h1 className="text-xl font-bold mb-4">Firecracker Quotation Management</h1>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div className="grid grid-cols-2 gap-4">
          <input className="border p-2" type="date" name="q_date" value={form.q_date} onChange={handleFormChange} />
          <input className="border p-2" name="supplier" placeholder="Supplier" value={form.supplier} onChange={handleFormChange} />
        </div>

        {form.products.map((p, idx) => (
          <div key={idx} className="grid grid-cols-4 gap-2 border p-2">
            <input className="border p-2" placeholder="Product" value={p.product_name} onChange={e => handleChange(e, idx, 'product_name')} />
            <input className="border p-2" placeholder="Rate" value={p.rate} onChange={e => handleChange(e, idx, 'rate')} />
            <input className="border p-2" placeholder="Discount %" value={p.discount} onChange={e => handleChange(e, idx, 'discount')} />
            <input className="border p-2" placeholder="Packing %" value={p.packing} onChange={e => handleChange(e, idx, 'packing')} />
            <input className="border p-2" placeholder="GST %" value={p.gst} onChange={e => handleChange(e, idx, 'gst')} />
            <input className="border p-2" placeholder="Purchase Rate" value={p.purchase_rate} onChange={e => handleChange(e, idx, 'purchase_rate')} />
            <input className="border p-2" placeholder="Piece Weight" value={p.piece_weight} onChange={e => handleChange(e, idx, 'piece_weight')} />
            <input className="border p-2" placeholder="Profit %" value={p.profit_percent} onChange={e => handleChange(e, idx, 'profit_percent')} />
          </div>
        ))}

        <button type="button" onClick={addProductRow} className="bg-yellow-400 px-4 py-2">+ Add Product</button>
        <button type="submit" className="bg-blue-500 text-white px-6 py-2">Save Quotation</button>
      </form>

      <div className="mt-10">
        <h2 className="text-lg font-semibold mb-2">Quotation Reports</h2>
        {quotations.map(q => (
          <div key={q.id} className="border p-4 mb-2">
            <div className="font-bold">#{q.id} | {q.q_date} | {q.supplier}</div>
            <table className="w-full mt-2 border">
              <thead>
                <tr className="bg-gray-200">
                  <th className="border px-2">Product</th>
                  <th className="border px-2">Rate</th>
                  <th className="border px-2">Disc%</th>
                  <th className="border px-2">Pack%</th>
                  <th className="border px-2">GST%</th>
                  <th className="border px-2">Purc Rate</th>
                  <th className="border px-2">Pcs Wt</th>
                  <th className="border px-2">Profit%</th>
                </tr>
              </thead>
              <tbody>
                {q.products.map((p, i) => (
                  <tr key={i}>
                    <td className="border px-2">{p.product_name}</td>
                    <td className="border px-2">{p.rate}</td>
                    <td className="border px-2">{p.discount}</td>
                    <td className="border px-2">{p.packing}</td>
                    <td className="border px-2">{p.gst}</td>
                    <td className="border px-2">{p.purchase_rate}</td>
                    <td className="border px-2">{p.piece_weight}</td>
                    <td className="border px-2">{p.profit_percent}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ))}
      </div>
    </div>
  )
}

export default App
